import React, { useEffect,useState } from "react";
import { useNavigation } from "../Router/Router";
import Axios from "axios";
import "./css/RecommandPage.css";
import { useLocation } from 'react-router-dom';
import io from 'socket.io-client';

const RecommandPage = () => {
    const location = useLocation();
    const [price,setPrice] = useState();
    const [products1, setProducts1] = useState([]);
    const [products2, setProducts2] = useState([]);
    const [products3, setProducts3] = useState([]);
    const { onClickStart, onClickOwner, onClickPay, onClickDone, onClickMain } =
      useNavigation();

    
      useEffect(() => {
        fetch('/get_random_products')
          .then(response => response.json())
          .then(data => {
            // 서버에서 받은 데이터 처리
            console.log(data);
      
            setProducts1([data.products[0].name, data.products[0].price, data.products[0].image]);
         //   setProducts2([data.products[1].name, data.products[1].price, data.products[1].image]);
          //  setProducts3([data.products[2].name, data.products[2].price, data.products[2].image]);
      
            // 업데이트된 상태를 확인
            console.log(products1);
           // console.log(products2);
           // console.log(products3);
          })
          .catch(error => {
            // 에러 처리
            console.error('데이터를 가져오는 중 오류 발생:', error);
          });
      }, []);
    return (
      <div className="wrapper1">
          <header>
            <div className="Header">
              <div className="goods">1. 상품확인</div>
              <div className="pay">2. 결제 방법</div>
              <div className="done">3. 결제 완료</div>
            </div>
          </header>
        <div className="wrapper">
        <div className="main">
            <div className="no">No</div>
            <div className="pic">사진</div>
            <div className="name">상품명</div>
            <div className="number">수량</div>
            <div className="price">가격</div>
          </div>
           
          <div className="recommendBackground">
            <div>
                잠시만요! 이런 상품도 있어요
       
                <div>오늘의 <span style={{color: '#d24232'}}>추천</span> 상품
                </div>
            </div>
            <div className="wrapperrec">
                <div className="firstdiv">

                </div>
                <div className="seconddiv">

                </div>
                <div className="thirddiv">

                </div>
            </div>
          </div>
        </div>
      </div>
    );
  };
  
  export default RecommandPage;
